graphql.query:
query getStudent($id: ID!){
    student(id: $id) {
        id
        name
        email
    }
}

query getStudent($id: ID!, $id2: ID!){
    dos: student(id: $id) {
       ...datosEstudiante
    }
    tres: student(id: $id2) {
       ...datosEstudiante
    }
}

fragment datosEstudiante on Student {
    id
    name
    email
}

