query getCourse($id: ID!){
    course(id: $id) {
        id
        title
        clases
        logo
        level
        path
        time
        students {
            id
            name
            email
        }
    }
}

query getCourses($idCursoUno: ID!, $idCursoDos: ID!){
    cursoUno: course(id: $idCursoUno) {
        ...cursoFragment
    }
    cursoDos: course(id: $idCursoDos) {
        ...cursoFragment
    }
}

fragment cursoFragment on Course {
    id
    title
    clases
    logo
    level
    path
    time
    students {
        id
        name
        email
    }
}

query getCourses($idCursoUno: ID!, $idCursoDos: ID!, $include: Boolean!){
    cursoUno: course(id: $idCursoUno) {
        ...cursoFragment
    }
    cursoDos: course(id: $idCursoDos) {
        ...cursoFragment
    }
}

fragment cursoFragment on Course {
    id
    title @include(if: $include)
    clases @include(if: $include)
    logo
    level
    path
    time
    students {
        id
        name
        email
    }
}

query getCourses($idCursoUno: ID!, $idCursoDos: ID!, $skip: Boolean!){
    cursoUno: course(id: $idCursoUno) {
        ...cursoFragment
    }
    cursoDos: course(id: $idCursoDos) {
        ...cursoFragment
    }
}

fragment cursoFragment on Course {
    id
    title @skip(if: $skip)
    clases @skip(if: $skip)
    logo
    level
    path
    time
    students {
        id
        name
        email
    }
}

mutation anadirCurso($course: CourseInput!){
    addCourse(course: $course) {
        id
        title
        description
    }
}

